// edit-clock.component.ts

import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { Timesheet } from '../../models/timesheet';

@Component({
  selector: 'app-edit-clock',
  templateUrl: './edit-clock.component.html',
  styleUrls: ['./edit-clock.component.css']
})
export class EditClockComponent implements OnInit {

  @Input() timesheet: Timesheet;
  @Input() clients: any[];
  @Input() tasks: any[];
  @Output() saveChangesEvent = new EventEmitter<Timesheet>();
  @Output() closeModalEvent = new EventEmitter<void>();

  constructor() { }

  ngOnInit(): void {
  }

  saveChanges(): void {
    this.saveChangesEvent.emit(this.timesheet);
  }

  closeModal(): void {
    this.closeModalEvent.emit();
  }

}
<!-- edit-timesheet.component.html -->

<div class="edit-timesheet-container">
  <h2>Edit Timesheet</h2>

  <div class="form-group">
    <label>Date</label>
    <input type="text" class="form-control" [(ngModel)]="timesheet.date">
  </div>

  <div class="form-group">
    <label>Billable</label>
    <input type="checkbox" class="form-control" [(ngModel)]="timesheet.billable">
  </div>

  <div class="form-group">
    <label>Project</label>
    <input type="text" class="form-control" [(ngModel)]="timesheet.project">
  </div>

  <div class="form-group">
    <label>Comment</label>
    <textarea class="form-control" rows="3" [(ngModel)]="timesheet.comment"></textarea>
  </div>

  <div class="form-group">
    <label>Start Time</label>
    <input type="text" class="form-control" [(ngModel)]="timesheet.start_time">
  </div>

  <div class="form-group">
    <label>End Time</label>
    <input type="text" class="form-control" [(ngModel)]="timesheet.end_time">
  </div>

  <div class="form-group">
    <label>Time Spent</label>
    <input type="text" class="form-control" [(ngModel)]="timesheet.time_spent">
  </div>

  <div class="form-group">
    <label>Client</label>
    <select class="form-control" [(ngModel)]="timesheet.client_id">
      <option *ngFor="let client of clients" [value]="client.id">{{ client.name }}</option>
    </select>
  </div>

  <div class="form-group">
    <label>Task</label>
    <select class="form-control" [(ngModel)]="timesheet.task_id">
      <option *ngFor="let task of tasks" [value]="task.id">{{ task.name }}</option>
    </select>
  </div>

  <button class="btn btn-primary" (click)="saveChanges()">Save Changes</button>
  <button class="btn btn-secondary" (click)="closeModal()">Cancel</button>
</div>
