import { Component } from '@angular/core';
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { DataloadService } from '../../services/api/dataload.service';
import {CalcTimeService} from "../../services/backend/calc-time.service";

@Component({
  selector: 'app-timesheet',
  templateUrl: './timesheet.component.html',
  styleUrls: ['./timesheet.component.css']
})
export class TimesheetComponent {

  message: string = '';
  userid: number = 0;
  timesheetData: any[] = [];
  clientData: any[] = [];
  taskData: any[] = [];
  selectedTimesheet: any = {}; // Declare the selectedTimesheet property and initialize as an empty object
  isEditing: boolean = false; // Track if editing is active

  constructor(private dls: DataloadService, private calcTimeService: CalcTimeService) { }

  ngOnInit() {
    this.dls.loadData().pipe(
      catchError((error: any) => {
        console.error(error);
        this.message = 'Failed to load data: ' + error.status;
        return throwError(error);
      })
    ).subscribe((response) => {
      // Set user id
      this.userid = response.userid;
      // Populate timesheet data
      this.timesheetData = response.timesheet;
      // Populate client data
      this.clientData = response.clients;
      // Populate task data
      this.taskData = response.tasks;
      // Get client name and task name based on IDs in timesheet data
      this.populateClientAndTaskNames();
    });
  }

  populateClientAndTaskNames() {
    for (let timesheet of this.timesheetData) {
      // Find client name based on client_id
      let client = this.clientData.find((client) => client.id === timesheet.client_id);
      timesheet.client_name = client ? client.Name : '';

      // Find task name based on task_id
      let task = this.taskData.find((task) => task.id === timesheet.task_id);
      timesheet.task_name = task ? task.Name : '';
    }
  }

  calculateSpentTime(){
    const startTime = this.selectedTimesheet.start_time;
    const endTime = this.selectedTimesheet.end_time;
    let spentTime = ''; // Initialize spentTime
    // Call the calculateSpentTime() function from the CalcTimeService with the spentTime argument
    this.selectedTimesheet.time_spent = this.calcTimeService.calculateSpentTime(startTime, endTime, spentTime);
  }


  openOverlay(timesheet: any) {
    this.selectedTimesheet = { ...timesheet }; // Create a copy of the timesheet object
    this.isEditing = true; // Activate editing mode
  }

  saveChanges() {
    console.log('Edited Timesheet:', this.selectedTimesheet);
    // Update the timesheet data with the edited values
    // You can implement the logic to update the data in your specific application
    // For example, you can make an API call to update the backend with the new values

    // After saving changes, deactivate editing mode and clear the selectedTimesheet
    this.isEditing = false;
    this.selectedTimesheet = {};
  }
closeOverlay() {
  this.isEditing = false;
  this.selectedTimesheet = null;
}
}
