export class TimesheetComponent {
  constructor(dls, calcTimeService, edts, datePipe) {
    this.dls = dls;
    this.calcTimeService = calcTimeService;
    this.edts = edts;
    this.datePipe = datePipe;
    this.message = '';
    this.userid = 0;
    this.timesheetData = [];
    this.clientData = [];
    this.taskData = [];
    this.selectedTimesheet = {
      date: '',
      billable: false,
      project: '',
      client_id: '',
      task_id: '',
      start_time: '',
      end_time: '',
      time_spent: '',
      comment: ''
    };
    this.isEditing = false;
    this.spentTime = '';
    this.filteredTimesheetData = [];
    this.filterType = '';
  }
  ngOnInit() {
    this.dls.loadData().pipe(catchError(error => {
      console.error(error);
      this.message = 'Failed to load data: ' + error.status;
      return throwError(error);
    })).subscribe(response => {
      this.userid = response.userid;
      this.timesheetData = response.timesheet;
      this.clientData = response.clients;
      this.taskData = response.tasks;
      this.populateClientAndTaskNames();
      this.timesheetData.reverse();
      this.filterByDay();
    });
  }
  formatDate(date) {
    if (date !== null) {
      return this.datePipe.transform(new Date(date), 'EEEE - dd - MMMM - yyyy') || '';
    }
    return '';
  }
  populateClientAndTaskNames() {
    for (let timesheet of this.timesheetData) {
      let client = this.clientData.find(client => client.id === timesheet.client_id);
      timesheet.client_name = client ? client.Name : '';
      let task = this.taskData.find(task => task.id === timesheet.task_id);
      timesheet.task_name = task ? task.Name : '';
    }
  }
  calculateSpentTime() {
    const startTime = this.selectedTimesheet.start_time;
    const endTime = this.selectedTimesheet.end_time;
    this.spentTime = this.calcTimeService.calculateSpentTime(startTime, endTime, this.selectedTimesheet.time_spent);
  }
  openOverlay(timesheet) {
    this.selectedTimesheet = {
      ...timesheet
    };
    this.isEditing = true;
    this.calculateSpentTime();
  }
  closeOverlay() {
    this.selectedTimesheet = {
      date: '',
      billable: false,
      project: '',
      client_id: '',
      task_id: '',
      start_time: '',
      end_time: '',
      time_spent: '',
      comment: ''
    };
    this.isEditing = false;
    this.spentTime = '';
  }
  saveChanges() {
    this.edts.submitForUpdate(this.selectedTimesheet).pipe(catchError(error => {
      console.error(error);
      this.message = 'Failed to submit timesheet: ' + error.status;
      return throwError(error);
    })).subscribe(response => {
      this.message = 'Timesheet submitted successfully!';
      // Update the timesheet data with the edited timesheet
      const index = this.timesheetData.findIndex(timesheet => timesheet.date === this.selectedTimesheet.date);
      if (index !== -1) {
        this.timesheetData[index] = {
          ...this.selectedTimesheet
        };
      }
      this.closeOverlay();
    });
  }
  filterByDay() {
    this.filterType = 'day';
    const uniqueDays = [...new Set(this.timesheetData.map(timesheet => timesheet.date))];
    this.filteredTimesheetData = uniqueDays.map(day => {
      const timesheets = this.timesheetData.filter(timesheet => timesheet.date === day);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: this.formatDate(day),
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }
  filterByMonth() {
    this.filterType = 'month';
    const uniqueMonths = [...new Set(this.timesheetData.map(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy-MM')))];
    this.filteredTimesheetData = uniqueMonths.map(month => {
      const timesheets = this.timesheetData.filter(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy-MM') === month);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: this.datePipe.transform(new Date(month), 'MMMM yyyy'),
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }
  filterByYear() {
    this.filterType = 'year';
    const uniqueYears = [...new Set(this.timesheetData.map(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy')))];
    this.filteredTimesheetData = uniqueYears.map(year => {
      const timesheets = this.timesheetData.filter(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy') === year);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: year,
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }
}
