import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { DataloadService } from '../../services/api/dataload.service';
import { CalcTimeService } from "../../services/backend/calc-time.service";
import { EdittimeService } from "../../services/api/edittime.service";
import { DatePipe } from '@angular/common';

interface Timesheet {
  date: string;
  billable: boolean;
  project: string;
  client_id: string;
  task_id: string;
  start_time: string;
  end_time: string;
  time_spent: string;
  comment: string;
  client_name?: string;
  task_name?: string;
}

interface editedTime {
  userid: number;
  edate: string;
  ebillable: boolean;
  eproject: string;
  eclient_id: string;
  etask_id: string;
  estart_time: string;
  eend_time: string;
  etime_spent: string;
  ecomment: string;
}

@Component({
  selector: 'app-timesheet',
  templateUrl: './timesheet.component.html',
  styleUrls: ['./timesheet.component.css']
})
export class TimesheetComponent {

  message: string = '';
  userid: number = 0;
  timesheetData: Timesheet[] = [];
  clientData: any[] = [];
  taskData: any[] = [];
  selectedTimesheet: Timesheet = {
    date: '',
    billable: false,
    project: '',
    client_id: '',
    task_id: '',
    start_time: '',
    end_time: '',
    time_spent: '',
    comment: ''
  };
  isEditing: boolean = false;
  spentTime: string = '';
  filteredTimesheetData: any[] = [];
  filterType: string = '';

  constructor(
    private dls: DataloadService,
    private calcTimeService: CalcTimeService,
    private edts: EdittimeService,
    private datePipe: DatePipe
  ) { }

  ngOnInit() {
    this.dls.loadData().pipe(
      catchError((error: any) => {
        console.error(error);
        this.message = 'Failed to load data: ' + error.status;
        return throwError(error);
      })
    ).subscribe((response) => {
      this.userid = response.userid;
      this.timesheetData = response.timesheet;
      this.clientData = response.clients;
      this.taskData = response.tasks;
      this.populateClientAndTaskNames();
      this.timesheetData.reverse();
      this.filterByDay();
    });
  }

  formatDate(date: string | null): string {
    if (date !== null) {
      return this.datePipe.transform(new Date(date), 'EEEE - dd - MMMM - yyyy') || '';
    }
    return '';
  }

  populateClientAndTaskNames() {
    for (let timesheet of this.timesheetData) {
      let client = this.clientData.find((client) => client.id === timesheet.client_id);
      timesheet.client_name = client ? client.Name : '';

      let task = this.taskData.find((task) => task.id === timesheet.task_id);
      timesheet.task_name = task ? task.Name : '';
    }
  }

  calculateSpentTime() {
    const startTime = this.selectedTimesheet.start_time;
    const endTime = this.selectedTimesheet.end_time;
    this.spentTime = this.calcTimeService.calculateSpentTime(startTime, endTime, this.selectedTimesheet.time_spent);
  }

  openOverlay(timesheet: Timesheet) {
    this.selectedTimesheet = { ...timesheet };
    this.isEditing = true;
    this.calculateSpentTime();
  }

  closeOverlay() {
    this.selectedTimesheet = {
      date: '',
      billable: false,
      project: '',
      client_id: '',
      task_id: '',
      start_time: '',
      end_time: '',
      time_spent: '',
      comment: ''
    };
    this.isEditing = false;
    this.spentTime = '';
  }

  saveChanges() {
    this.edts.submitForUpdate(this.selectedTimesheet as editedTime).pipe(
      catchError((error: any) => {
        console.error(error);
        this.message = 'Failed to submit timesheet: ' + error.status;
        return throwError(error);
      })
    ).subscribe((response) => {
      this.message = 'Timesheet submitted successfully!';
      // Update the timesheet data with the edited timesheet
      const index = this.timesheetData.findIndex((timesheet) => timesheet.date === this.selectedTimesheet.date);
      if (index !== -1) {
        this.timesheetData[index] = { ...this.selectedTimesheet };
      }
      this.closeOverlay();
    });
  }

  filterByDay() {
    this.filterType = 'day';
    const uniqueDays = [...new Set(this.timesheetData.map(timesheet => timesheet.date))];
    this.filteredTimesheetData = uniqueDays.map(day => {
      const timesheets = this.timesheetData.filter(timesheet => timesheet.date === day);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: this.formatDate(day),
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }

  filterByMonth() {
    this.filterType = 'month';
    const uniqueMonths = [...new Set(this.timesheetData.map(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy-MM')))];
    this.filteredTimesheetData = uniqueMonths.map(month => {
      const timesheets = this.timesheetData.filter(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy-MM') === month);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: this.datePipe.transform(new Date(month), 'MMMM yyyy'),
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }

  filterByYear() {
    this.filterType = 'year';
    const uniqueYears = [...new Set(this.timesheetData.map(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy')))];
    this.filteredTimesheetData = uniqueYears.map(year => {
      const timesheets = this.timesheetData.filter(timesheet => this.datePipe.transform(new Date(timesheet.date), 'yyyy') === year);
      const totalSpentTime = timesheets.reduce((sum, timesheet) => sum + this.calcTimeService.parseTime(timesheet.time_spent), 0);
      return {
        title: year,
        timesheets,
        totalSpentTime: this.calcTimeService.formatTime(totalSpentTime)
      };
    });
  }

}
<h1>Timesheet</h1>

<div class="filter-buttons">
  <button (click)="filterByYear()">Year</button>
  <button (click)="filterByMonth()">Month</button>
  <button (click)="filterByDay()">Day</button>
</div>

<table *ngIf="filterType === 'year'">
  <thead>
    <tr>
      <th>Date</th>
      <th>Billable</th>
      <th>Project</th>
      <th>Client</th>
      <th>Task</th>
      <th>Start Time</th>
      <th>End Time</th>
      <th>Spent Time</th>
      <th>Comment</th>
      <th>Edit</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let timesheet of filteredTimesheetData">
      <td>{{ formatDate(timesheet.date) }}</td>
      <td>{{ timesheet.billable ? 'Yes' : 'No' }}</td>
      <td>{{ timesheet.project }}</td>
      <td>{{ timesheet.client_name }}</td>
      <td>{{ timesheet.task_name }}</td>
      <td>{{ timesheet.start_time }}</td>
      <td>{{ timesheet.end_time }}</td>
      <td>{{ timesheet.time_spent }}</td>
      <td>{{ timesheet.comment }}</td>
      <td><button (click)="openOverlay(timesheet)">Edit</button></td>
    </tr>
  </tbody>
</table>

<div *ngIf="filterType === 'month'">
  <div *ngFor="let monthData of filteredTimesheetData">
    <h2>{{ formatDate(monthData.date) }}</h2>
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Billable</th>
          <th>Project</th>
          <th>Client</th>
          <th>Task</th>
          <th>Start Time</th>
          <th>End Time</th>
          <th>Spent Time</th>
          <th>Comment</th>
          <th>Edit</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let timesheet of monthData.timesheets">
          <td>{{ formatDate(timesheet.date) }}</td>
          <td>{{ timesheet.billable ? 'Yes' : 'No' }}</td>
          <td>{{ timesheet.project }}</td>
          <td>{{ timesheet.client_name }}</td>
          <td>{{ timesheet.task_name }}</td>
          <td>{{ timesheet.start_time }}</td>
          <td>{{ timesheet.end_time }}</td>
          <td>{{ timesheet.time_spent }}</td>
          <td>{{ timesheet.comment }}</td>
          <td><button (click)="openOverlay(timesheet)">Edit</button></td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<div *ngIf="filterType === 'day'">
  <div *ngFor="let dayData of filteredTimesheetData">
    <h2>{{ dayData.dayOfWeek }}</h2>
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Billable</th>
          <th>Project</th>
          <th>Client</th>
          <th>Task</th>
          <th>Start Time</th>
          <th>End Time</th>
          <th>Spent Time</th>
          <th>Comment</th>
          <th>Edit</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let timesheet of dayData.timesheets">
          <td>{{ formatDate(timesheet.date) }}</td>
          <td>{{ timesheet.billable ? 'Yes' : 'No' }}</td>
          <td>{{ timesheet.project }}</td>
          <td>{{ timesheet.client_name }}</td>
          <td>{{ timesheet.task_name }}</td>
          <td>{{ timesheet.start_time }}</td>
          <td>{{ timesheet.end_time }}</td>
          <td>{{ timesheet.time_spent }}</td>
          <td>{{ timesheet.comment }}</td>
          <td><button (click)="openOverlay(timesheet)">Edit</button></td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<div *ngIf="isEditing">
  <div class="overlay">
    <div class="overlay-content">
      <h2>Edit Timesheet</h2>
      <form>
        <label>Date:</label>
        <input type="text" [(ngModel)]="selectedTimesheet.date" readonly>

        <label>Billable:</label>
        <input type="checkbox" [(ngModel)]="selectedTimesheet.billable">

        <label>Project:</label>
        <input type="text" [(ngModel)]="selectedTimesheet.project">

        <label>Client:</label>
        <select [(ngModel)]="selectedTimesheet.client_id">
          <option *ngFor="let client of clientData" [value]="client.id">{{ client.Name }}</option>
        </select>

        <label>Task:</label>
        <select [(ngModel)]="selectedTimesheet.task_id">
          <option *ngFor="let task of taskData" [value]="task.id">{{ task.Name }}</option>
        </select>

        <label>Start Time:</label>
        <input type="time" [(ngModel)]="selectedTimesheet.start_time">

        <label>End Time:</label>
        <input type="time" [(ngModel)]="selectedTimesheet.end_time">

        <label>Time Spent:</label>
        <input type="text" [(ngModel)]="selectedTimesheet.time_spent" (change)="calculateSpentTime()">

        <label>Spent Time:</label>
        <input type="text" [(ngModel)]="spentTime" readonly>

        <label>Comment:</label>
        <textarea [(ngModel)]="selectedTimesheet.comment"></textarea>

        <div class="button-container">
          <button (click)="saveChanges()">Save</button>
          <button (click)="closeOverlay()">Cancel</button>
        </div>
      </form>
    </div>
  </div>
</div>








<div class="modal" [class.hidden]="!isEditing">
  <div class="modal-content">

    <!-- Display the selected row data inside the modal -->
    <h2>Edit Clock</h2>
    <div class="form-group">
      <label for="clockId">Clock ID: </label>
      <input type="text" id="clockId" [(ngModel)]="selectedTimesheet.entry_no" [disabled]="true" />
    </div>
    <div class="form-group">
      <label for="date">Date: </label>
      <input type="date" id="date" [(ngModel)]="selectedTimesheet.date" />
    </div>
    <div class="form-group">
      <label for="billable">Billable: </label>
      <select id="billable" [(ngModel)]="selectedTimesheet.billable">
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
    </div>
    <div class="form-group">
      <label for="project">Project: </label>
      <input type="text" id="project" [(ngModel)]="selectedTimesheet.project" />
    </div>
    <div class="form-group">
      <label for="client">Client: </label>
      <select id="client" [(ngModel)]="selectedTimesheet.client_name">
        <option *ngFor="let client of clientData" [value]="client.Name">{{ client.Name }} - {{ client.Description }}</option>
      </select>
    </div>
    <div class="form-group">
      <label for="task">Task: </label>
      <select id="task" [(ngModel)]="selectedTimesheet.task_name">
        <option *ngFor="let task of taskData" [value]="task.Name">{{ task.Name }} - {{ task.Description }}</option>
      </select>
    </div>
    <div class="form-group">
      <label for="startTime">Start Time: </label>
      <input type="time" id="startTime" [(ngModel)]="selectedTimesheet.start_time" (change)="calculateSpentTime()" />
    </div>
    <div class="form-group">
      <label for="endTime">End Time: </label>
      <input type="time" id="endTime" [(ngModel)]="selectedTimesheet.end_time" (change)="calculateSpentTime()"/>
    </div>
    <div class="form-group">
      <label for="spentTime">Spent Time: </label>
      <span id="spentTime">{{spentTime}}</span>
    </div>
    <div class="form-group">
      <label for="comment">Comment: </label>
      <input type="text" id="comment" [(ngModel)]="selectedTimesheet.comment" />
    </div>
    <!-- Add save and cancel buttons for updating the timesheet -->
    <div class="buttons">
      <button class="save-button" (click)="saveChanges()">Save</button>
      <button class="close-button" (click)="closeOverlay()">Close</button>
    </div>
  </div>
</div>
