import { Component } from '@angular/core';
import {catchError} from "rxjs/operators";
import {throwError} from "rxjs";
import {DataloadService} from "../../services/api/dataload.service";

@Component({
  selector: 'app-timesheet',
  templateUrl: './timesheet.component.html',
  styleUrls: ['./timesheet.component.css']
})
export class TimesheetComponent {

  message:string = '';
  userid:number = 0;
  entry:number = 0;

  timesheetData: any[] = [];
  constructor(private dls:DataloadService) { }


    ngOnInit() {
  this.dls.loadData().pipe(
    catchError((error: any) => {
      console.error(error);
      this.message = 'Failed to load data: ' + error.status;
      return throwError(error);
    })
  ).subscribe((response) => {
    console.log(response);
    // Set user id
    this.userid = response.userid;
    // Populate timesheet data
    this.timesheetData = response.timesheet;
  });
}


}
<table>
  <thead>
    <tr>
      <th>Clock ID</th>
      <th>Date</th>
      <th>Billable</th>
      <th>Project</th>
      <th>Client</th>
      <th>Task</th>
      <th>Start Time</th>
      <th>End Time</th>
      <th>Spent Time</th>
      <th>Comment</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let timesheet of timesheetData">
      <td>{{ timesheet.entry_no }}</td>
      <td>{{ timesheet.date }}</td>
      <td>{{ timesheet.billable }}</td>
      <td>{{ timesheet.project }}</td>
      <td>{{ timesheet.client }}</td>
      <td>{{ timesheet.task }}</td>
      <td>{{ timesheet.start_time }}</td>
      <td>{{ timesheet.end_time }}</td>
      <td>{{ timesheet.time_spent }}</td>
      <td>{{ timesheet.comment }}</td>
    </tr>
  </tbody>
</table>
