import { Component } from '@angular/core';
import { catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { DataloadService } from '../../services/api/dataload.service';
import { CalcTimeService } from "../../services/backend/calc-time.service";
import { EdittimeService } from "../../services/api/edittime.service";
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-timesheet',
  templateUrl: './timesheet.component.html',
  styleUrls: ['./timesheet.component.css']
})
export class TimesheetComponent {

  message: string = '';
  userid: number = 0;
  timesheetData: any[] = [];
  clientData: any[] = [];
  taskData: any[] = [];
  selectedTimesheet: any = {};
  isEditing: boolean = false;
  spentTime: string = '';
  filteredTimesheetData: any[] = [];
  filterType: string = '';

  constructor(
    private dls: DataloadService,
    private calcTimeService: CalcTimeService,
    private edts: EdittimeService,
    private datePipe: DatePipe
  ) { }

  ngOnInit() {
    this.dls.loadData().pipe(
      catchError((error: any) => {
        console.error(error);
        this.message = 'Failed to load data: ' + error.status;
        return throwError(error);
      })
    ).subscribe((response) => {
      this.userid = response.userid;
      this.timesheetData = response.timesheet;
      this.clientData = response.clients;
      this.taskData = response.tasks;
      this.populateClientAndTaskNames();
      this.timesheetData.reverse();
      this.updateFilteredTimesheetData();
    });
  }

  formatDate(date: string | null): string {
    if (date !== null) {
      return this.datePipe.transform(new Date(date), 'EEEE - dd - MMMM - yyyy') || '';
    }
    return '';
  }

  populateClientAndTaskNames() {
    for (let timesheet of this.timesheetData) {
      let client = this.clientData.find((client) => client.id === timesheet.client_id);
      timesheet.client_name = client ? client.Name : '';

      let task = this.taskData.find((task) => task.id === timesheet.task_id);
      timesheet.task_name = task ? task.Name : '';
    }
  }

  calculateSpentTime() {
    const startTime = this.selectedTimesheet.start_time;
    const endTime = this.selectedTimesheet.end_time;
    this.spentTime = this.calcTimeService.calculateSpentTime(startTime, endTime, this.selectedTimesheet.spentTime);
  }

  openOverlay(timesheet: any) {
    this.selectedTimesheet = { ...timesheet };
    this.spentTime = '';
    this.isEditing = true;
  }

  saveChanges() {
    console.log('Edited Timesheet:', this.selectedTimesheet);
    this.edts.submitForUpdate(this.selectedTimesheet).pipe(
      catchError((error: any) => {
        console.error('Failed to save timesheet data:', error);
        return throwError(error);
      })
    ).subscribe((response) => {
      console.log('Timesheet data saved:', response);
      this.isEditing = false;
      this.selectedTimesheet = {};
      this.updateFilteredTimesheetData();
    });
  }

  closeOverlay() {
    this.isEditing = false;
    this.selectedTimesheet = null;
    this.updateFilteredTimesheetData();
  }

  filterByYear() {
    this.filterType = 'year';
    const currentYear = new Date().getFullYear();
    this.filteredTimesheetData = this.timesheetData.filter((timesheet) => {
      const timesheetYear = new Date(timesheet.start_time).getFullYear();
      return timesheetYear === currentYear;
    });
  }

  filterByMonth() {
    this.filterType = 'month';
    const currentMonth = new Date().getMonth();
    const currentYear = new Date().getFullYear();
    this.filteredTimesheetData = this.timesheetData.filter((timesheet) => {
      const timesheetMonth = new Date(timesheet.start_time).getMonth();
      const timesheetYear = new Date(timesheet.start_time).getFullYear();
      return timesheetMonth === currentMonth && timesheetYear === currentYear;
    });
  }

  filterByDay() {
    this.filterType = 'day';
    const currentDate = new Date().getDate();
    const currentMonth = new Date().getMonth();
    const currentYear = new Date().getFullYear();
    this.filteredTimesheetData = this.timesheetData.filter((timesheet) => {
      const timesheetDate = new Date(timesheet.start_time).getDate();
      const timesheetMonth = new Date(timesheet.start_time).getMonth();
      const timesheetYear = new Date(timesheet.start_time).getFullYear();
      return timesheetDate === currentDate && timesheetMonth === currentMonth && timesheetYear === currentYear;
    });
  }

  updateFilteredTimesheetData() {
    if (this.filterType === 'year') {
      this.filterByYear();
    } else if (this.filterType === 'month') {
      this.filterByMonth();
    } else if (this.filterType === 'day') {
      this.filterByDay();
    }
  }

}

<h1>Timesheet</h1>

<div class="filter-buttons">
  <button (click)="filterByYear()">Year</button>
  <button (click)="filterByMonth()">Month</button>
  <button (click)="filterByDay()">Day</button>
</div>

<table *ngIf="filterType === 'year'">
  <thead>
    <tr>
      <th>Date</th>
      <th>Billable</th>
      <th>Project</th>
      <th>Client</th>
      <th>Task</th>
      <th>Start Time</th>
      <th>End Time</th>
      <th>Spent Time</th>
      <th>Comment</th>
      <th>Edit</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let timesheet of filteredTimesheetData">
      <td>{{ formatDate(timesheet.date) }}</td>
      <td>{{ timesheet.is_billable ? 'Yes' : 'No' }}</td>
      <td>{{ timesheet.project_name }}</td>
      <td>{{ timesheet.client_name }}</td>
      <td>{{ timesheet.task_name }}</td>
      <td>{{ timesheet.start_time }}</td>
      <td>{{ timesheet.end_time }}</td>
      <td>{{ timesheet.spent_time }}</td>
      <td>{{ timesheet.comment }}</td>
      <td>
        <button (click)="openOverlay(timesheet)">Edit</button>
      </td>
    </tr>
  </tbody>
</table>

<div *ngIf="filterType === 'month'">
  <div *ngFor="let day of filteredTimesheetData">
    <h2>{{ day.date }}</h2>
    <table>
      <thead>
        <tr>
          <th>Billable</th>
          <th>Project</th>
          <th>Client</th>
          <th>Task</th>
          <th>Start Time</th>
          <th>End Time</th>
          <th>Spent Time</th>
          <th>Comment</th>
          <th>Edit</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let timesheet of day.timesheets">
