import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-edit-overlay',
  templateUrl: './edit-overlay.component.html',
  styleUrls: ['./edit-overlay.component.css']
})
export class EditOverlayComponent {
  @Input() timesheet: any | null = null;
  @Output() onSave: EventEmitter<any> = new EventEmitter<any>();
  @Output() onCancel: EventEmitter<void> = new EventEmitter<void>();

  saveTimesheet(): void {
    // Emit the edited timesheet data to save
    this.onSave.emit(this.timesheet);
  }

  cancelEdit(): void {
    // Emit an event to cancel the edit and close the overlay
    this.onCancel.emit();
  }
}
<div class="overlay">
  <div class="overlay-content">
    <h2>Edit Timesheet</h2>
    <div>
      <label for="project">Project:</label>
      <input id="project" type="text" [(ngModel)]="timesheet.project">
    </div>
    <div>
      <label for="comment">Comment:</label>
      <textarea id="comment" [(ngModel)]="timesheet.comment"></textarea>
    </div>
    <!-- Add more input fields for other timesheet properties -->

    <div class="overlay-buttons">
      <button (click)="saveTimesheet()">Save</button>
      <button (click)="cancelEdit()">Cancel</button>
    </div>
  </div>
</div>
