import { Component } from '@angular/core';
import {catchError} from "rxjs/operators";
import {throwError} from "rxjs";
import {DataloadService} from "../../services/api/dataload.service";

@Component({
  selector: 'app-timesheet',
  templateUrl: './timesheet.component.html',
  styleUrls: ['./timesheet.component.css']
})
export class TimesheetComponent {

  message:string = '';
  userid:number = 0;
  timesheetData: any[] = [];
  constructor(private dls:DataloadService) { }
    ngOnInit() {
  this.dls.loadData().pipe(
    catchError((error: any) => {
      console.error(error);
      this.message = 'Failed to load data: ' + error.status;
      return throwError(error);
    })
  ).subscribe((response) => {
    console.log(response);
    // Set user id
    this.userid = response.userid;
    // Populate timesheet data
    this.timesheetData = response.timesheet;
  });
}


}
